import React, { Component } from 'react';
import {initialPlaces} from '../places.js'
import Place from './Place.js';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import ButtonGroup from 'react-bootstrap/ButtonGroup'

//import DB from 'taffydb';
//TOOD use import 
var DB = require( '../../node_modules/taffydb/taffy.js' ).taffy;
require('datejs')

class PlacesGrid extends Component {
	constructor() {
		super();

		this.loadPlaces = this.loadPlaces.bind( this );
		var db = DB(initialPlaces);
		var date = new Date();
		console.log(new Date.today().addDays(1));

		var places = db();
		this.state = {
			places: places,
			db: db,
		};
	};

	loadPlaces(variable) {
		var places = this.state.db({'type_delivery':variable});
		this.setState({ places: places });
	}
	render() {
		return (
			<Container>
				<ButtonGroup>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('Home Delivery')}>Home Delivery</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('Concorde2')}>Concorde2</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('The Old Market')}>The Old Market</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('Marlborough Theather')}>Marlborough Theather</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('Komedia')}>Komedia</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadPlaces('The Green Door Store')}>The Green Door Store</button></div>
				</ButtonGroup>
				<CardGroup>
				{this.state.places.map((obj, index) => <Place
					meta={obj}
					style={{
						width: '236px',
						height: `${index % 2 === 0 ? 4 * 50 : 50 }px`,
						display: 'block',
						background: 'rgba(0,0,0,0.7)'
					}}
					/>)}
				</CardGroup>
			</Container>
			);
	}
}

export default PlacesGrid;
