import React, { Component } from 'react';
import {initialEvents} from '../events.js'
import Event from './Event.js';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import ButtonGroup from 'react-bootstrap/ButtonGroup'
//import DB from 'taffydb';
//TOOD use import 
var DB = require( '../../node_modules/taffydb/taffy.js' ).taffy;


class EventGrid extends Component {
	constructor() {
		super();

		this.loadEvents = this.loadEvents.bind( this );
		var db = DB(initialEvents);

		var events = db({'location':'Komedia', 'dateISO':'2018-09-15'});
		this.state = {
			events: events,
			db: db,
		};
	};

	loadEvents(venue) {
		var events = this.state.db({'location':venue});
		this.setState({ events: events });
	}
	render() {
		return (
			<Container>
				<ButtonGroup>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('Brighton Dome')}>Brighton Dome</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('Concorde2')}>Concorde2</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('The Old Market')}>The Old Market</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('Marlborough Theather')}>Marlborough Theather</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('Komedia')}>Komedia</button></div>
					<div className=""><button className="btn btn-primary" onClick={() => this.loadEvents('The Green Door Store')}>The Green Door Store</button></div>
				</ButtonGroup>
				<Row className="show-grid">
				{this.state.events.map((obj, index) => <Event
					meta={obj}
					style={{
						width: '236px',
						height: `${index % 2 === 0 ? 4 * 50 : 50 }px`,
						display: 'block',
						background: 'rgba(0,0,0,0.7)'
					}}
					/>)}
				</Row>
			</Container>
			);
	}
}

export default EventGrid;
