import React, { Component } from 'react';
import Col from 'react-bootstrap/Col';
import _ from 'underscore';

class Event extends Component {
	loadImage(img, w, h) {
		if(_.isUndefined(img) || _.isEmpty(img) || !img.startsWith("http")) {
			img = 'http://placehold.it/'+ w + 'x' + h;
		}
		img = img.replace("background-image:url(","");
		img = img.replace(")","");
		return img;
	}

	render() {
		return (
			<Col className="card mb-3" sm={6} md={3} location={ this.props.meta.location }>
				<div className="card-body">
					<h5 className="card-title"></h5>
					<h6 className="ard-subtitle text-muted">{ this.props.meta.date } at <span class="badge badge-pill badge-warning { this.props.meta.color }">{ this.props.meta.location }</span></h6>
				</div>
				<img width="100%" height="200px" display="block" src={ this.props.meta.pictures } alt={ this.props.meta.title } />
				<div className="card-body">
    				<p className="card-text">{ this.props.meta.notes }</p>
  				</div>
  				<div className="card-body">
				    <a href="{ this.props.meta.website }" className="card-link">Website</a>
				</div>
			</Col>
		);
	}
}

export default Event;
